from app import app
from flask import render_template
import random


@app.route('/')
def index():
    return render_template('index.html')

@app.route('/user/<name>')
def getUser(name):
    return render_template('user.html', name=name)

@app.route('/galeria')
def getGallery():
     ogorki = [
          'href = data:image/jpeg;base64,'
     ]

     random.shuffle(ogorki)

     return render_template('galeria.html',ogorki=ogorki)



